import java.util.Scanner;public class Calculation implements Runnable{    public void run() {        Scanner sc = new Scanner(System.in);        String result = null;        calcGetInfo();        String s = sc.nextLine();        String operator = "+-*/";        String[] operands;        if (s.contains(" - ")) {            operands = s.split(" - ");        } else {            operands = s.split("[+*/]");        }        if(!operands[0].contains("\""))throw new IllegalArgumentException("Ошибка: первый оператор должен быть строкой ");        String operand1 = operands[0].replace("\"", "").replace(" ", "");        String operand2 = operands[1].replace("\"", "").replace(" ", "");        operator = getOperator(s, operator);        switch (operator) {            case "+":                result = operand1.concat(operand2);                break;            case "-":                result = operand1.replace(operand2, "");                break;            case "*":                StringBuilder builder = new StringBuilder(operand1);                operands[1] = operands[1].replace(" ", "");                if (!operands[1].contains("\"")) {                    int n = parse(operands[1]);                    while (n != 1) {                        builder.append(operand1);                        --n;                    }                } else {                    throw new IllegalArgumentException("Строку на строку нельзя умножать!");                }                result = builder.toString();                break;            case "/":                operands[1] = operands[1].replace(" ", "");                int n = parse(operands[1]);                if (!operands[1].contains("\"")) {                    int operandLength = operand1.length() / n;                    result = operand1.substring(0, operandLength);                } else {                    throw new IllegalArgumentException("Строку на строку нельзя делить!");                }                break;            default:                System.out.println("Оператор не найден");        }        if (result.length() > 40) result = result.substring(0, 40) + "...";            System.out.println("\"" + result + "\"");    }    String getOperator(String input, String operator) {        String[] sop = operator.split("");        for (String result : sop) {            if (input.contains(result)) {                int indexChar = input.indexOf(result);                operator = String.valueOf(input.charAt(indexChar));            }        }        return operator;    }    static void calcGetInfo()    {        System.out.println("*** Калькулятор умеет выполнять операции");        System.out.println("*** Cложение строк, вычитания строки из строки, умножения строки на число и деления строки на число");        System.out.println("*** \"a\" + \"b\", \"a\" - \"b\", \"a\" * b, \"a\" / b");        System.out.println("Ввод:");    }    private int parse(String s)    {        int result = Integer.parseInt(s);        if(result < 1 || result > 10) {            throw new IllegalArgumentException("ОШИБКА: Числа могут быть только от 1 до 10");        }        else        {            return result;        }    }}